// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import {LibDiamond} from "../libraries/LibDiamond.sol";
import {LibKeyChain} from "../libraries/LibKeyChain.sol";
import "../interfaces/IVotingFacet.sol";
import "../interfaces/IUsageFacet.sol";
import "../libraries/math/SafeMath.sol";

contract VotingFacet is IVotingFacet {
    using SafeMath for uint256;

    event Vote(
        address indexed voter,
        uint256 indexed proposalIndex,
        uint256 optionIndex,
        uint256 votingPowers
    );
    event RecallVote(address indexed recaller, uint256 indexed proposalIndex);

    function isParticipated(uint256 _proposalIndex)
        external
        view
        override
        returns (bool)
    {
        return _isParticipated(_proposalIndex, msg.sender);
    }

    function isUserParticipated(uint256 _proposalIndex, address user)
        external
        view
        returns (bool)
    {
        return _isParticipated(_proposalIndex, user);
    }

    function _isParticipated(uint256 proposalIndex, address voter)
        internal
        view
        returns (bool)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        return ds.participates[proposalIndex][voter];
    }

    function _isLimit(uint256 proposalIndex, uint256[] memory votingPowers)
        internal
        view
        returns (bool)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[proposalIndex];

        bool isLimited = true;
        uint256 count = 0;

        for (uint256 i = 0; i < proposal.optionsCount; i++) {
            if (votingPowers[i] > 0) {
                count = count.add(1);
            }
            if (count > proposal.voteLimit) {
                isLimited = false;
                break;
            }
        }
        return isLimited;
    }

    function vote(uint256 _proposalIndex, uint256[] calldata _votingPowers)
        external
        override
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[_proposalIndex];

        //check array size
        require(
            _votingPowers.length == proposal.optionsCount,
            "Wrong array size"
        );
        require(
            _isLimit(_proposalIndex, _votingPowers),
            "Vote more than Vote limit choice"
        );

        //check start-end time
        require(block.timestamp >= proposal.startTimestamp, "Vote not start");
        require(block.timestamp <= proposal.endTimestamp, "Vote end");

        //case1: new voter (not Participate)
        if (!_isParticipated(_proposalIndex, msg.sender)) {
            for (uint256 i = 0; i < proposal.optionsCount; i++) {
                if (_votingPowers[i] > 0) {
                    _addVoteToproposal(
                        _proposalIndex,
                        i,
                        _votingPowers[i],
                        msg.sender
                    );
                    //Increase Voter Count
                    ds.voterCount[_proposalIndex][msg.sender] = ds
                    .voterCount[_proposalIndex][msg.sender].add(1);
                }
            }
            ds.participates[_proposalIndex][msg.sender] = true;
        }
        //case2: Participated
        else {
            for (uint256 i = 0; i < proposal.optionsCount; i++) {
                // //case2.1: vote equal vote limit
                if (
                    (ds.currentVotePowers[_proposalIndex][i][msg.sender] > 0) &&
                    (_votingPowers[i] > 0)
                ) {
                    _addVoteToproposal(
                        _proposalIndex,
                        i,
                        _votingPowers[i],
                        msg.sender
                    );
                    //case2.2: vote less than vote limit
                } else if (
                    (ds.voterCount[_proposalIndex][msg.sender] <
                        proposal.voteLimit) && (_votingPowers[i] > 0)
                ) {
                    _addVoteToproposal(
                        _proposalIndex,
                        i,
                        _votingPowers[i],
                        msg.sender
                    );
                    //Increase Voter Count
                    ds.voterCount[_proposalIndex][msg.sender] = ds
                    .voterCount[_proposalIndex][msg.sender].add(1);

                    //case2.3: Invalid vote
                } else if (_votingPowers[i] > 0) {
                    revert("Invalid Vote");
                }
            }
        }
    }

    function _addVoteToproposal(
        uint256 proposalIndex,
        uint256 optionIndex,
        uint256 votingPowers,
        address voter
    ) internal {
        address VFINIX = LibDiamond.getAddress(
            keccak256(abi.encodePacked(LibKeyChain.KEY_VFINIX_ADDRESS))
        );

        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[proposalIndex];

        uint256 minimunVotingPower = proposal.minimumVotingPower;
        uint256 availableVotes = IUsageFacet(VFINIX).getAvailableVotes(voter);

        //check vote available
        require(
            (votingPowers >= minimunVotingPower) &&
                (availableVotes >= votingPowers),
            "Invalid Vote"
        );

        IUsageFacet(VFINIX).addVotesToService(
            keccak256(
                abi.encodePacked((LibKeyChain.KEY_VFINIX_VOTING_SERVICE_ID))
            ),
            voter,
            votingPowers
        );

        //Increase Total Voting power
        proposal.totalVotingPower = proposal.totalVotingPower.add(votingPowers);

        //Increase Option Voting power
        proposal.optionVotingPower[optionIndex] = proposal
            .optionVotingPower[optionIndex]
            .add(votingPowers);

        //Increase Option Voter power
        ds.currentVotePowers[proposalIndex][optionIndex][voter] = ds
        .currentVotePowers[proposalIndex][optionIndex][voter].add(votingPowers);

        //Increase Option Voter power
        ds.votedPowers[proposalIndex][optionIndex][voter] = ds
        .votedPowers[proposalIndex][optionIndex][voter].add(votingPowers);

        emit Vote(voter, proposalIndex, optionIndex, votingPowers);
    }

    function isClaimable(uint256 _proposalIndex)
        external
        view
        override
        returns (bool)
    {
        return _isClaimable(_proposalIndex);
    }

    function _isClaimable(uint256 proposalIndex) internal view returns (bool) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[proposalIndex];
        bool isEmptyVoted = false;
        for (uint256 i = 0; i < proposal.optionsCount; i++) {
            if (ds.currentVotePowers[proposalIndex][i][msg.sender] > 0) {
                isEmptyVoted = true;
                break;
            }
        }
        return isEmptyVoted;
    }

    function isUserClaimable(uint256 _proposalIndex, address user) external view returns (bool) {
        return _isUserClaimable(_proposalIndex, user);
    }

    function _isUserClaimable(uint256 proposalIndex, address user) internal view returns (bool) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[proposalIndex];
        bool claimable = false;
        for (uint256 i = 0; i < proposal.optionsCount; i++) {
            if (ds.currentVotePowers[proposalIndex][i][user] > 0) {
                claimable = true;
                break;
            }
        }
        return claimable;
    }

    function recallVotesFromProposal(uint256 _proposalIndex) external override {
        address VFINIX = LibDiamond.getAddress(
            keccak256(abi.encodePacked(LibKeyChain.KEY_VFINIX_ADDRESS))
        );

        uint256 totalUsingVotes = IUsageFacet(VFINIX).getTotalUsingVotes(
            msg.sender
        );

        require(totalUsingVotes > 0, "No voted in proposals");

        require(_isClaimable(_proposalIndex), "No vote in this proposal");

        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        LibDiamond.Proposal storage proposal = ds.proposals[_proposalIndex];

        require(block.timestamp > proposal.endTimestamp, "Vote not end");

        uint256 usingVotes;
        for (uint256 i = 0; i < proposal.optionsCount; i++) {
            uint256 optionVotedPower = ds.currentVotePowers[_proposalIndex][i][
                msg.sender
            ];
            ds.currentVotePowers[_proposalIndex][i][msg.sender] = ds
            .currentVotePowers[_proposalIndex][i][msg.sender].sub(
                    optionVotedPower
                );

            usingVotes = usingVotes.add(optionVotedPower);
        }

        IUsageFacet(VFINIX).removeVotesFromService(
            keccak256(
                abi.encodePacked(LibKeyChain.KEY_VFINIX_VOTING_SERVICE_ID)
            ),
            msg.sender,
            usingVotes
        );
        emit RecallVote(msg.sender, _proposalIndex);
    }
}
